using System;
using System.Security.Cryptography.X509Certificates;

using Fido2NetLib;

using Xunit;

namespace Test
{
    public class CryptoUtilsTests
    {
        [Fact]
        public void TestCertInCRLFalseCase()
        {
            byte[] certBytes = Convert.FromBase64String("");

            byte[] crl = Convert.FromBase64String("");

            var cert = new X509Certificate2(certBytes);

            Assert.False(CryptoUtils.IsCertInCRL(crl, cert));
        }

        [Fact]
        public void TestCertInCRLTrueCase()
        {
            byte[] certBytes = Convert.FromBase64String("MIIDAjCCAqigAwIBAgIPBIBnSd4we71RdaJtc /  + 59Ah1hNfL5OEtxdgy +/ kIJyiScl4 + T8o4IBBTCCAQEwCwYDVR0PBAQDAgbAMAwGA1UdEwEB / wQCMAAwHQYDVR0OBBYEFPl4RxJ2M8prAEvqnSFK4 +  + 5VPY5gDcPXAdwA9Pudwe1M0BcGsa5 + gIhAJ0opi4Y / w26gNaAvsCvalwCqI6QYQCP1bjGSMgu3K1e");

            byte[] crl = Convert.FromBase64String("");

            var cert = new X509Certificate2(certBytes);

            Assert.True(CryptoUtils.IsCertInCRL(crl, cert));
        }
    }
}
